{-# LANGUAGE OverloadedStrings #-}
module Xlsx.Simple.Internal
    (
    styles,
    xText,
    xDate,
    xDouble,
    xDfltColWidth,
    xDfltRowHeight
    ) where

import qualified Data.Map as M
import Codec.Xlsx
import Data.Time
import Codec.Xlsx.Writer
import Data.ByteString.Lazy

xText   i t   = CellData (Just i) (Just $ CellText t) 

xDate :: Int -> LocalTime -> CellData
xDate   i t   = CellData (Just i) (Just $ CellLocalTime t) 

xDouble :: Int -> Double -> CellData
xDouble i t   = CellData (Just i) (Just $ CellDouble t) 

xDfltColWidth :: ColumnsWidth
xDfltColWidth = ColumnsWidth 10 20 15.0 0

xDfltRowHeight :: RowHeights
xDfltRowHeight =  M.fromList [(1,(RowProps (Just 15.0) (Just 0)))]

